import React, {useEffect, useState} from "react";
import styled from "styled-components";
import Movies from "backend/movies";
import {useUser} from "../hook/User";
import {useParams} from "react-router-dom";

const StyledDiv = styled.div`
  display: flex;
  flex-direction: column;
  backgroundImage
`

const MovieDetail = () => {
    const { accessToken } = useUser();
    const [ movie, setMovie ] = React.useState(0);
    const { movieId } = useParams();

    const styles = {
        background: {
            backgroundImage: `url(${"https://image.tmdb.org/t/p/original" + movie.backdropPath}`,
            height: '100vh',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover'
        }
    }

    const getMovie = () => {
        Movies.getMovieDetail({}, movieId, accessToken)
            .then(response => {

                setMovie(response.data.movie);
                console.log(response.data.movie);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)))

    }

    useEffect(() => getMovie(), []);



    return (
        <div style={styles.background}>
            <StyledDiv >
                <h1>{movie.title}</h1>
                <h4>Year: {movie.year}</h4>
                <h4>Director: {movie.director}</h4>
                <img src={"https://image.tmdb.org/t/p/w300" + movie.posterPath} />
                <img src={"https://image.tmdb.org/t/p/w300" + movie.backdropPath} />
            </StyledDiv>
        </div>

    );
}

export default MovieDetail;
