import React, {useEffect, useState} from "react";
import styled from "styled-components";
import Movies from "backend/movies";
import {useUser} from "../hook/User";
import {useParams} from "react-router-dom";

const StyledDivTitleColumn = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const StyledDivColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 5px;
`

const StyledDivRow = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;
`

const StyledH1 = styled.p`
    
`

const MovieDetail = () => {
    const { accessToken } = useUser();
    const [ movie, setMovie ] = React.useState(0);
    const { movieId } = useParams();

    const getMovie = () => {
        Movies.getMovieDetail({}, movieId, accessToken)
            .then(response => {
                setMovie(response.data.movie);
                console.log(response.data.movie);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)))

    }

    useEffect(() => getMovie(), []);

    return (
        <StyledDivTitleColumn >
            <h1>{movie.title}</h1>
            <br/>

            <StyledDivRow>
                <img src={"https://image.tmdb.org/t/p/w300" + movie.posterPath} />
                <StyledDivColumn>
                    <h4>Year: {movie.year}</h4>
                    <h4>Director: {movie.director}</h4>
                    <h4>IMDB Rating: {movie.rating}/10</h4>
                    <h4>Overview:
                        <p>{movie.overview}</p></h4>
                </StyledDivColumn>
            </StyledDivRow>


        </StyledDivTitleColumn>
    );
}

export default MovieDetail;
