import React, {useEffect, useState} from "react";
import styled from "styled-components";
import Movies from "backend/movies";
import {useUser} from "../hook/User";
import {useParams} from "react-router-dom";

const Title = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  max-width: 500px;
`

const Row = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;
  padding-top: 15px;
`

const Text = styled.div`
  display: flex;
  gap: .3rem;
  text-align: center;
`

const MovieDetail = () => {
    const { accessToken } = useUser();
    const [ movie, setMovie ] = React.useState(0);
    const [ genres, setGenres ] = React.useState(0);
    const { movieId } = useParams();

    const getMovie = () => {
        Movies.getMovieDetail({}, movieId, accessToken)
            .then(response => {
                setMovie(response.data.movie);
                setGenres(response.data.genres);
                console.log(response.data.genres);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)))

    }

    useEffect(() => getMovie(), []);

    return (
        <Title >
            <h1>{movie.title}</h1>
            <br/>

            <Row>
                <img src={"https://image.tmdb.org/t/p/w300" + movie.posterPath} />
                <Column>
                    <Text>
                        <h4>Year: </h4>
                        <p>{movie.year}</p>
                    </Text>
                    <Text>
                        <h4>Director: </h4>
                        <p>{movie.director}</p>
                    </Text>
                    <Text>
                        <h4>IMDB Rating: </h4>
                        <p>{movie.rating}/10</p>
                    </Text>
                    <Text>
                        <h4>Genre(s): </h4>
                        {genres.map(genre =>
                            <Text className="movie-detail"><span>Genre:</span> {genre.name}</Text>
                        )}
                    </Text>
                    <br/>
                    <h4>Overview</h4>
                    <p>{movie.overview}</p>
                </Column>
            </Row>
        </Title>
    );
}

export default MovieDetail;
