import React, {useEffect, useState} from "react";
import styled from "styled-components";
import Movies from "backend/movies";
import {useUser} from "../hook/User";
import {useParams} from "react-router-dom";

const StyledDivTitleColumn = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

const StyledDivColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  max-width: 500px;
`

const StyledDivRow = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;
  padding-top: 50px;
`

const StyledText = styled.div`
  display: flex;
  gap: .3rem;
  text-align: center;
`

const MovieDetail = () => {
    const { accessToken } = useUser();
    const [ movie, setMovie ] = React.useState(0);
    const { movieId } = useParams();

    const getMovie = () => {
        Movies.getMovieDetail({}, movieId, accessToken)
            .then(response => {
                setMovie(response.data.movie);
                console.log(response.data.movie);
            })
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)))

    }

    useEffect(() => getMovie(), []);

    return (
        <StyledDivTitleColumn >
            <h1>{movie.title}</h1>
            <br/>

            <StyledDivRow>
                <img src={"https://image.tmdb.org/t/p/w300" + movie.posterPath} />
                <StyledDivColumn>
                    <StyledText>
                        <h4>Year: </h4>
                        <p>{movie.year}</p>
                    </StyledText>
                    <StyledText>
                        <h4>Director: </h4>
                        <p>{movie.director}</p>
                    </StyledText>
                    <StyledText>
                        <h4>IMDB Rating: </h4>
                        <p>{movie.rating}/10</p>
                    </StyledText>
                    <br/>
                    <h4>Overview</h4>
                    <p>{movie.overview}</p>



                </StyledDivColumn>
            </StyledDivRow>


        </StyledDivTitleColumn>
    );
}

export default MovieDetail;
